import { AfterViewInit, Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-valor',
  templateUrl: './valor.component.html',
  styleUrls: ['./valor.component.css']
})
export class ValorComponent implements OnInit, AfterViewInit {
  valor: number;
  constructor(){
    this.valor = 0;
  }

  ngOnInit(): void {}

  ngAfterViewInit(){
      var elems = document.querySelectorAll('select');
      M.FormSelect.init(elems,{});
  }

  onSelectChange(event: Event){
    this.valor = +(event.target as HTMLInputElement).value;
  }

  onButtonClick(){
    alert(`O valor doado é ${this.valor}`);
  }

  onButtonClickAgain(){
    alert('Muito Obrigado');
  }

  onEnterKey(){
    this.onButtonClick();
  }
}
<div class="container contact">
  <h5>Doação</h5>
  <h6>As doações são importantes para manter nossa página funcionando!</h6>
  <hr/>
  <div class="row">
    <div class="col s12">
      <div
        id="msg-saque"
        class="card-panel red white-text"
        style="display: none"
      ></div>
    </div>
  </div>
  <form class="col s12" >
    <div class="row">
      <div class="col s12">
        <select (change)="this.onSelectChange($event)">
          <option value="-1" selected>Escolha um valor</option>
          <option value="10">R$ 10,00</option>
          <option value="25">R$ 25,00</option>
          <option value="50">R$ 50,00</option>
          <option value="100">R$ 100,00</option>
          <option value="1000">R$ 1000,00</option>
        </select>
      </div>
    </div>
    <div class="row">
      <div class="input-field col s12">        
        <input
          name="valor-input"
          type="text"
          [value]="this.valor"
          (keydown.enter)="onEnterKey()"
        />
        <label class="active" for="valor-input">Valor de doação</label>
      </div>
    </div>
    <button
      class="btn waves-effect waves-light amber darken-4"
      type="button"
      (click)="this.onButtonClick(); this.onButtonClickAgain()"
    >
      Doar
      <i class="material-icons right">send</i>
    </button>
  </form>
</div>
