import { Component, EventEmitter,Input,OnChanges,OnInit,Output,SimpleChanges} from '@angular/core';

@Component({
  selector: 'app-saldo',
  templateUrl: './saldo.component.html',
  styleUrls: ['./saldo.component.css']
})
export class SaldoComponent implements OnInit, OnChanges{
  @Input() value:number = 0;
  @Output() donationEvent = new EventEmitter<boolean>();
  backgroundColor = 'amber';

  constructor(){}

  ngOnChanges(): void {
    if(this.value > 10000)
    setTimeout(() => {

      this.donationEvent.emit(true);
  })

  }

}
<div class="row">
  <div
    class="col s12 m6 offset-m3 card-painel white-text"
    id="div-painel"
    [ngClass]="this.backgroundColor"
  >
    {{ this.value | currency : "BRL" }}
  </div>
</div>
