import { Component, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { User } from './../model/User';

@Component({
  selector: 'app-cadastro-usuarios',
  templateUrl: './cadastro-usuarios.component.html',
  styleUrls: ['./cadastro-usuarios.component.css']
})

export class CadastroUsuariosComponent implements OnInit{

  @ViewChild('form') form!: NgForm;

  user!: User;
  users?: User[];

  ngOnInit(): void {
    this.user = new User('', '');
  }



}
<div class="container contact">
    <h5>Cadastro de Usuários</h5>
    <h6>Entre com o nome de usuario e senha</h6>
    <hr />
    <div class="row">
        <div class="col s12">
          <div
            id="msg-login"
            class="card-panel white-text"
            style="display: none"
          >
          </div>
        </div>
<form class="col s12" #form="ngForm" (submit)="onSubmit()">
<div class="input-field col s12">
    <input
      id="input-username"
      name="input-username"
      type="text"
      class="validate"
      [(ngModel)]="this.user.username"
      pattern="^[a-z]+([\.-]?[a-z]+)*"
      #username="ngModel"
      required
    />
    <label for="input-username">Nome de Usuário *</label>
    <div class="red-text" [hidden]="username.valid || username.untouched">
      Por favor, informe o seu username em letras minúsculas e sem espaços.
    </div>
 </div>
